mapper.insert:
insert into data_base (
	type, name, user_name, password, 
	host, port, driver, char_set, 
	user_id, create_time
) values (
	#{type}, #{name}, #{userName}, #{password}, 
	#{host}, #{port}, #{driver}, #{charSet}, 
	#{userId}, #{createTime}
)

mapper.select:
select  from data_base

	and id = #{id}
	and type = #{type}
	and name = #{name}
	and user_name = #{userName}
	and password = #{password}
	and host = #{host}
	and port = #{port}
	and driver = #{driver}
	and char_set = #{charSet}
	and user_id = #{userId}
	and create_time = #{createTime}

select  from data_base

	and id = #{t.id}
	and type = #{t.type}
	and name = #{t.name}
	and user_name = #{t.userName}
	and password = #{t.password}
	and host = #{t.host}
	and port = #{t.port}
	and driver = #{t.driver}
	and char_set = #{t.charSet}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from data_base

	and id = #{id}
	and type = #{type}
	and name = #{name}
	and user_name = #{userName}
	and password = #{password}
	and host = #{host}
	and port = #{port}
	and driver = #{driver}
	and char_set = #{charSet}
	and user_id = #{userId}
	and create_time = #{createTime}

mapper.update:
update data_base
type = #{type}, name = #{name}, user_name = #{userName}, password = #{password}, host = #{host}, port = #{port}, driver = #{driver}, char_set = #{charSet}, user_id = #{userId}, create_time = #{createTime},
where id = #{id}
