mapper.insert:
insert into dict_config (
	value, type, label, sort, 
	parent_id, group_id, user_id, create_time
) values (
	#{value}, #{type}, #{label}, #{sort}, 
	#{parentId}, #{groupId}, #{userId}, #{createTime}
)

mapper.select:
select  from dict_config

	and id = #{id}
	and value = #{value}
	and type = #{type}
	and label = #{label}
	and sort = #{sort}
	and parent_id = #{parentId}
	and group_id = #{groupId}
	and user_id = #{userId}
	and create_time = #{createTime}

select  from dict_config

	and id = #{t.id}
	and value = #{t.value}
	and type = #{t.type}
	and label = #{t.label}
	and sort = #{t.sort}
	and parent_id = #{t.parentId}
	and group_id = #{t.groupId}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from dict_config

	and id = #{id}
	and value = #{value}
	and type = #{type}
	and label = #{label}
	and sort = #{sort}
	and parent_id = #{parentId}
	and group_id = #{groupId}
	and user_id = #{userId}
	and create_time = #{createTime}

mapper.update:
update dict_config
value = #{value}, type = #{type}, label = #{label}, sort = #{sort}, parent_id = #{parentId}, group_id = #{groupId}, user_id = #{userId}, create_time = #{createTime},
where id = #{id}
