mapper.insert:
insert into index_column (
	idx_id, col_name, data_type, sort, 
	table_id, db_id, user_id, create_time
) values (
	#{idxId}, #{colName}, #{dataType}, #{sort}, 
	#{tableId}, #{dbId}, #{userId}, #{createTime}
)

mapper.select:
select  from index_column

	and id = #{id}
	and idx_id = #{idxId}
	and col_name = #{colName}
	and data_type = #{dataType}
	and sort = #{sort}
	and table_id = #{tableId}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

select  from index_column

	and id = #{t.id}
	and idx_id = #{t.idxId}
	and col_name = #{t.colName}
	and data_type = #{t.dataType}
	and sort = #{t.sort}
	and table_id = #{t.tableId}
	and db_id = #{t.dbId}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from index_column

	and id = #{id}
	and idx_id = #{idxId}
	and col_name = #{colName}
	and data_type = #{dataType}
	and sort = #{sort}
	and table_id = #{tableId}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

mapper.update:
update index_column
idx_id = #{idxId}, col_name = #{colName}, data_type = #{dataType}, sort = #{sort}, table_id = #{tableId}, db_id = #{dbId}, user_id = #{userId}, create_time = #{createTime},
where id = #{id}
