mapper.select:
select  from table_column

	and id = #{id}
	and table_id = #{tableId}
	and db_id = #{dbId}
	and name = #{name}
	and label = #{label}
	and data_type = #{dataType}
	and data_length = #{dataLength}
	and sort = #{sort}
	and nullable = #{nullable}
	and auto_increment = #{autoIncrement}
	and default_value = #{defaultValue}
	and comment = #{comment}
	and edit_element = #{editElement}
	and data = #{data}
	and data_from = #{dataFrom}
	and add_type = #{addType}
	and update_type = #{updateType}
	and query_type = #{queryType}
	and user_id = #{userId}
	and create_time = #{createTime}

select  from table_column

	and id = #{t.id}
	and table_id = #{t.tableId}
	and db_id = #{t.dbId}
	and name = #{t.name}
	and label = #{t.label}
	and data_type = #{t.dataType}
	and data_length = #{t.dataLength}
	and sort = #{t.sort}
	and nullable = #{t.nullable}
	and auto_increment = #{t.autoIncrement}
	and default_value = #{t.defaultValue}
	and comment = #{t.comment}
	and edit_element = #{t.editElement}
	and data = #{t.data}
	and data_from = #{t.dataFrom}
	and add_type = #{t.addType}
	and update_type = #{t.updateType}
	and query_type = #{t.queryType}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from table_column

	and id = #{id}
	and table_id = #{tableId}
	and db_id = #{dbId}
	and name = #{name}
	and label = #{label}
	and data_type = #{dataType}
	and data_length = #{dataLength}
	and sort = #{sort}
	and nullable = #{nullable}
	and auto_increment = #{autoIncrement}
	and default_value = #{defaultValue}
	and comment = #{comment}
	and edit_element = #{editElement}
	and data = #{data}
	and data_from = #{dataFrom}
	and add_type = #{addType}
	and update_type = #{updateType}
	and query_type = #{queryType}
	and user_id = #{userId}
	and create_time = #{createTime}

mapper.update:
update table_column
table_id = #{tableId}, db_id = #{dbId}, name = #{name}, label = #{label}, data_type = #{dataType}, data_length = #{dataLength}, sort = #{sort}, nullable = #{nullable}, auto_increment = #{autoIncrement}, default_value = #{defaultValue}, comment = #{comment}, edit_element = #{editElement}, data = #{data}, data_from = #{dataFrom}, add_type = #{addType}, update_type = #{updateType}, query_type = #{queryType}, user_id = #{userId}, create_time = #{createTime},
where id = #{id}
