mapper.insert:
insert into table_index (
	table_id, name, type, db_id, 
	user_id, create_time
) values (
	#{tableId}, #{name}, #{type}, #{dbId}, 
	#{userId}, #{createTime}
)

mapper.select:
select  from table_index

	and id = #{id}
	and table_id = #{tableId}
	and name = #{name}
	and type = #{type}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

select  from table_index

	and id = #{t.id}
	and table_id = #{t.tableId}
	and name = #{t.name}
	and type = #{t.type}
	and db_id = #{t.dbId}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from table_index

	and id = #{id}
	and table_id = #{tableId}
	and name = #{name}
	and type = #{type}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

mapper.update:
update table_index
table_id = #{tableId}, name = #{name}, type = #{type}, db_id = #{dbId}, user_id = #{userId}, create_time = #{createTime},
where id = #{id}
