mapper.insert:
insert into table_info (
	name, label, engine, charset, 
	comment, cache, ret_primary, au_show, 
	detail_show, db_name, db_id, user_id, create_time
) values (
	#{name}, #{label}, #{engine}, #{charset}, 
	#{comment}, #{cache}, #{retPrimary}, #{auShow}, 
	#{detailShow}, #{dbName}, #{dbId}, #{userId}, #{createTime}
)

mapper.select:
select  from table_info

	and id = #{id}
	and name = #{name}
	and label = #{label}
	and engine = #{engine}
	and charset = #{charset}
	and comment = #{comment}
	and cache = #{cache}
	and ret_primary = #{retPrimary}
	and au_show = #{auShow}
	and detail_show = #{detailShow}
	and db_name = #{dbName}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

select  from table_info

	and id = #{t.id}
	and name = #{t.name}
	and label = #{t.label}
	and engine = #{t.engine}
	and charset = #{t.charset}
	and comment = #{t.comment}
	and cache = #{t.cache}
	and ret_primary = #{t.retPrimary}
	and au_show = #{t.auShow}
	and detail_show = #{t.detailShow}
	and db_name = #{t.dbName}
	and db_id = #{t.dbId}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from table_info

	and id = #{id}
	and name = #{name}
	and label = #{label}
	and engine = #{engine}
	and charset = #{charset}
	and comment = #{comment}
	and cache = #{cache}
	and ret_primary = #{retPrimary}
	and au_show = #{auShow}
	and detail_show = #{detailShow}
	and db_name = #{dbName}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

mapper.update:
update table_info
name = #{name}, label = #{label}, engine = #{engine}, charset = #{charset}, comment = #{comment}, cache = #{cache}, ret_primary = #{retPrimary}, au_show = #{auShow}, detail_show = #{detailShow}, db_name = #{dbName}, db_id = #{dbId}, user_id = #{userId}, create_time = #{createTime},
where id = #{id}
