mapper.insert:
insert into table_method (
	name, request_type, path_variable, table_id, 
	db_id, user_id, create_time
) values (
	#{name}, #{requestType}, #{pathVariable}, #{tableId}, 
	#{dbId}, #{userId}, #{createTime}
)

mapper.select:
select  from table_method

	and id = #{id}
	and name = #{name}
	and request_type = #{requestType}
	and path_variable = #{pathVariable}
	and table_id = #{tableId}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

select  from table_method

	and id = #{t.id}
	and name = #{t.name}
	and request_type = #{t.requestType}
	and path_variable = #{t.pathVariable}
	and table_id = #{t.tableId}
	and db_id = #{t.dbId}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from table_method

	and id = #{id}
	and name = #{name}
	and request_type = #{requestType}
	and path_variable = #{pathVariable}
	and table_id = #{tableId}
	and db_id = #{dbId}
	and user_id = #{userId}
	and create_time = #{createTime}

mapper.update:
update table_method
name = #{name}, request_type = #{requestType}, path_variable = #{pathVariable}, table_id = #{tableId}, db_id = #{dbId}, user_id = #{userId}, create_time = #{createTime},
where id = #{id}
