mapper.insert:
insert into user_account (
	user_name, password, salt, role_id, 
	user_id, create_time, login_time, status
) values (
	#{userName}, #{password}, #{salt}, #{roleId}, 
	#{userId}, #{createTime}, #{loginTime}, #{status}
)

mapper.select:
select  from user_account

	and id = #{id}
	and user_name = #{userName}
	and password = #{password}
	and salt = #{salt}
	and role_id = #{roleId}
	and user_id = #{userId}
	and create_time = #{createTime}
	and login_time = #{loginTime}
	and status = #{status}

select  from user_account

	and id = #{t.id}
	and user_name = #{t.userName}
	and password = #{t.password}
	and salt = #{t.salt}
	and role_id = #{t.roleId}
	and user_id = #{t.userId}
	and create_time = #{t.createTime}
	and login_time = #{t.loginTime}
	and status = #{t.status}

order by id desc
limit #{page.pageNo}, #{page.pageSize}

select count(*) from user_account

	and id = #{id}
	and user_name = #{userName}
	and password = #{password}
	and salt = #{salt}
	and role_id = #{roleId}
	and user_id = #{userId}
	and create_time = #{createTime}
	and login_time = #{loginTime}
	and status = #{status}

mapper.update:
update user_account
user_name = #{userName}, password = #{password}, salt = #{salt}, role_id = #{roleId}, user_id = #{userId}, create_time = #{createTime}, login_time = #{loginTime}, status = #{status},
where id = #{id}
